import React from 'react';
import axios from './axios'
import './App.css';

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			sourcecode : "",
			result : ""
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange  = this.handleChange.bind(this);
		this.clear = this.clear.bind(this);
	}

	async handleSubmit(event){
		event.preventDefault();
		var data = {
			source : this.state.sourcecode
		}
		if(this.state.sourcecode !== "") {		
			await axios.post('/compile',data,{header:{'Content-Type': 'application/json'}}).then((responce)=>{
				if(responce.data.result.stderr === ""){
					var data = responce.data.result.stdout;
					this.setState({
						result : data
					})
				} else {
					this.setState({
						result:responce.data.result.stderr
					})
				}
			})
		}
	}

	handleChange(event) {
		this.setState({
			sourcecode : event.target.value
		})
	}

	clear() {
		this.setState({
			sourcecode : "",
			result : ""
		})
	}

	render() {	
	  return (
	    <>
        <div className="container">
		    <div className="row">
			    <div className="col-8 col1">
			    	<form onSubmit={this.handleSubmit}>
				        <div className="form-group">
						    <label htmlFor="exampleFormControlTextarea1">Enter Your Javascript Code here...</label>
						    <textarea onChange = {this.handleChange} value = {this.state.sourcecode} className="form-control text" id="exampleFormControlTextarea1" rows="3"></textarea>
						</div>
						<button type="submit" className="btn btn-outline-success">Compile</button>&nbsp;
						<button type="button" onClick={this.clear} className="btn btn-outline-danger">Clear</button>
					</form>
			    </div>
			    <div className="col-4 col2">
			      	<div className="form-group">
					    <label htmlFor="exampleFormControlTextarea1">Result</label>
					    <textarea disabled value = {this.state.result} className="form-control text" id="exampleFormControlTextarea1" rows="3"></textarea>
					</div>
			    </div>
			</div>
		</div>
	    </>
	  );
	}
}

export default App;
