import React from 'react';
import './App.css';

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			sourcecode : ""
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange  = this.handleChange.bind(this);
	}


	handleSubmit(event){
		event.preventDefault();
		console.log(this.state.sourcecode);
	}

	handleChange(event) {
		this.setState({
			sourcecode : event.target.value
		})
	}

	render() {	
	  return (
	    <>
        <div className="container-fluid">
		    <div className="row">
			    <div className="col-8 col1">
			    	<form onSubmit={this.handleSubmit}>
			        <div className="form-group">
					    <label htmlFor="exampleFormControlTextarea1">Enter Code Here...</label>
					    <textarea onChange = {this.handleChange} value = {this.state.sourcecode} className="form-control text" id="exampleFormControlTextarea1" rows="3"></textarea>
					</div>
					<button type="submit" className="btn btn-primary">Compile</button>
					</form>
			    </div>
			    <div className="col col2">
			      	<div className="form-group">
					    <label htmlFor="exampleFormControlTextarea1">Enter Code Here...</label>
					    <textarea className="form-control text" id="exampleFormControlTextarea1" rows="3"></textarea>
					</div>
			    </div>
			</div>
		</div>
	    </>
	  );
	}
}

export default App;
