import React from 'react';
import axios from './axios'
import './App.css';

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			sourcecode : "",
			result : ""
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange  = this.handleChange.bind(this);
	}

	async handleSubmit(event){
		event.preventDefault();
		var data = {
			source : this.state.sourcecode
		}
		await axios.post('/compile',data,{header:{'Content-Type': 'application/json'}}).then((responce)=>{
			if(responce.data.status){
				var data = responce.data.result.stdout;
				this.setState({
					result : data
				})
			} else {
				this.setState({
					error:responce.data.error
				})
			}
		})
	}

	handleChange(event) {
		this.setState({
			sourcecode : event.target.value
		})
	}

	render() {	
	  return (
	    <>
        <div className="container">
		    <div className="row">
			    <div className="col-8 col1">
			    	<form onSubmit={this.handleSubmit}>
			        <div className="form-group">
					    <label htmlFor="exampleFormControlTextarea1">Enter Your Javascript Code here...</label>
					    <textarea onChange = {this.handleChange} value = {this.state.sourcecode} className="form-control text" id="exampleFormControlTextarea1" rows="3"></textarea>
					</div>
					<button type="submit" className="btn btn-tomato">Compile</button>&nbsp;
					<button type="button" className="btn btn-primary">Clear</button>
					</form>
			    </div>
			    <div className="col col2">
			      	<div className="form-group">
					    <label htmlFor="exampleFormControlTextarea1">Result</label>
					    <textarea disabled value = {this.state.result} className="form-control text" id="exampleFormControlTextarea1" rows="3"></textarea>
					</div>
			    </div>
			</div>
		</div>
	    </>
	  );
	}
}

export default App;
