import React from 'react';
import axios from './axios'
import './App.css';

class App extends React.Component {

	constructor(props) {
		super(props);
		this.state = {
			sourcecode : "",
			result : ""
		}
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange  = this.handleChange.bind(this);
		this.clear = this.clear.bind(this);
		this.add = this.add.bind(this);
	}

	async handleSubmit(event){
		event.preventDefault();
		var data = {
			source : this.state.sourcecode
		}
		if(this.state.sourcecode !== "") {		
			await axios.post('/compile',data,{header:{'Content-Type': 'application/json'}}).then((responce)=>{
				if(responce.data.result.stderr === ""){
					var data = responce.data.result.stdout;
					this.setState({
						result : data
					})
				} else {
					this.setState({
						result:responce.data.result.stderr
					})
				}
			})
		}
	}

	handleChange(event) {
		this.setState({
			sourcecode : event.target.value
		})
	}

	clear() {
		this.setState({
			sourcecode : "",
			result : ""
		})
	}



	add(event) {

		HTMLTextAreaElement.prototype.getCaretPosition = function () { //return the caret position of the textarea
    		return this.selectionStart;
		};
		HTMLTextAreaElement.prototype.setCaretPosition = function (position) { //change the caret position of the textarea
		    this.selectionStart = position;
		    this.selectionEnd = position;
		    this.focus();
		};
		HTMLTextAreaElement.prototype.hasSelection = function () { //if the textarea has selection then return true
		    if (this.selectionStart == this.selectionEnd) {
		        return false;
		    } else {
		        return true;
		    }
		};
		HTMLTextAreaElement.prototype.getSelectedText = function () { //return the selection text
		    return this.value.substring(this.selectionStart, this.selectionEnd);
		};
		HTMLTextAreaElement.prototype.setSelection = function (start, end) { //change the selection area of the textarea
		    this.selectionStart = start;
		    this.selectionEnd = end;
		    this.focus();
		};
		console.log(event.keyCode);
		var textarea = document.getElementsByClassName('text');
        if (event.keyCode === 9) { //tab was pressed
        var newCaretPosition;
        newCaretPosition = textarea.getCaretPosition() + "    ".length;
        textarea.value = textarea.value.substring(0, textarea.getCaretPosition()) + "    " + textarea.value.substring(textarea.getCaretPosition(), textarea.value.length);
        textarea.setCaretPosition(newCaretPosition);
        return false;
    }
        // event.preventDefault();
	}

	render() {	
	  return (
	    <>
        <div className="container">
		    <div className="row">
			    <div className="col-sm-8">
			    	<form onSubmit={this.handleSubmit}>
				        <div className="form-group">
						    <label htmlFor="exampleFormControlTextarea1">Enter Your Javascript Code here...</label>
						    <textarea onKeyDown={this.add} onChange = {this.handleChange} value = {this.state.sourcecode} className="form-control text" id="exampleFormControlTextarea1" rows="3"></textarea>
						</div>
						<button type="submit" className="btn btn-outline-success">Compile</button>&nbsp;
						<button type="button" onClick={this.clear} className="btn btn-outline-danger">Clear</button>
					</form>
			    </div>
			    <div className="col-sm-4">
			      	<div className="form-group">
					    <label htmlFor="exampleFormControlTextarea1">Result</label>
					    <textarea disabled value = {this.state.result} className="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
					</div>
			    </div>
			</div>
		</div>
	    </>
	  );
	}
}

export default App;
